        Presale,
        PublicSale, 
        SoldOut,
        Reveal
    }

    string public baseURI;

    string public notRevealedURI;

    Step public sellingStep;

    bytes32 public merkleRoot;

    uint private constant MAX_SUPPLY = 20;
    uint private constant MAX_WhiteList = 10;
    uint private constant MAX_FREE = 10;

    uint public pricePresale = 0.01 ether; 

    uint public publicSalePrice = 0.02 ether;

    uint public zero_price = 0 ether;


    bool public revealed = false;

    bool public paused = false;

    string public baseExtension = ".json";

    Counters.Counter private _nftIdCounter;

    uint public saleStartTime = 1650326745;

    mapping(address => uint) public amountNFTsperWalletWhitelistSale;
    mapping(address => uint) public amountNFTperWalletFreeMint;

    uint private teamLength;

    constructor(string memory _theBaseURI, string memory _theNotRevealedURI, bytes32 _merkleRoot) 
    ERC721A("Doggykong NFT", "DKG")
     {
        _nftIdCounter.increment();
        baseURI = _theBaseURI;
        notRevealedURI = _theNotRevealedURI;
        merkleRoot = _merkleRoot;
    }

